import React, { useRef } from 'react';
import { groupStoriesByUser } from '../utils/imageUtils';

const StoriesBar = ({ stories, onAddStory, onStoryClick }) => {
  const fileInputRef = useRef(null);

  const handleAddClick = () => {
    fileInputRef.current?.click();
  };

  const handleFileChange = async (e) => {
    const file = e.target.files[0];
    if (file && (file.type === 'image/jpeg' || file.type === 'image/png')) {
      await onAddStory(file);
      e.target.value = ''; // Reset input
    }
  };

  return (
    <div className="w-full bg-white dark:bg-gray-900 border-b border-gray-200 dark:border-gray-700">
      <div className="max-w-4xl mx-auto px-4 py-4">
        <div className="flex items-center space-x-4 overflow-x-auto scrollbar-hide">
          {/* Add Story Button */}
          <div className="flex-shrink-0">
            <button
              onClick={handleAddClick}
              className="w-16 h-16 rounded-full border-2 border-gray-300 dark:border-gray-600 bg-gray-100 dark:bg-gray-800 flex items-center justify-center hover:bg-gray-200 dark:hover:bg-gray-700 transition-colors"
            >
              <svg className="w-6 h-6 text-gray-500 dark:text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
              </svg>
            </button>
            <input
              ref={fileInputRef}
              type="file"
              accept="image/jpeg,image/png"
              onChange={handleFileChange}
              className="hidden"
            />
          </div>

          {/* Story Circles */}
          {Object.entries(groupStoriesByUser(stories)).map(([userId, userData], index) => (
            <div key={userId} className="flex-shrink-0 text-center">
              <button
                onClick={() => onStoryClick(userId)}
                className="w-16 h-16 rounded-full border-2 border-blue-500 p-0.5 hover:border-blue-600 transition-colors mb-1"
              >
                <div className="w-full h-full rounded-full overflow-hidden bg-gray-200 dark:bg-gray-700">
                  <img
                    src={userData.stories[0].image_base64}
                    alt="Story"
                    className="w-full h-full object-cover"
                  />
                </div>
              </button>
              <p className="text-xs text-gray-600 dark:text-gray-400 truncate w-16">
                {userData.username}
              </p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default StoriesBar;
